package p2022_06_22;

public class Oper01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 산술연산자 +, -, *, /, %(나머지)
		
		int a = 10, b = 3, c;
		
		c = a+b;
		System.out.println("a+b="+c);
		System.out.println("a+b="+(a+b));
		System.out.println("a-b="+(a-b));
		System.out.println("a*b="+(a*b));
		System.out.println("a/b="+(a/b));		// int형과 int형으로 산술연산할시 int형값을 반환함
		System.out.println("a%b="+(a%b));
		

	}

}
