package p2022_06_22;

public class Oper11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 증감연산자 : ++ , --
		
		int a=10, b=10;
		
		System.out.println("a="+a++);
		System.out.println("a="+a);
		
		System.out.println("b="+(++b));
		System.out.println("b="+b);
	}

}
