package p2022_06_23;

public class For02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//
		int sum=0;
		for(int i=1 ; i <= 10; i++) {
			
			sum = sum+i;

			System.out.println("sum의값은="+sum);
		}
		System.out.println("sum의값은="+sum);

	}

}
