package p2022_06_23;

public class While01 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
//		 while 문
//		 while(조건식)
//		{
//			반복 실행할 문장;
//		}
		
		// while 문을 이용해서 '사랑해요' 10번 출력하는 프로그램을 작성하세요.
		
		int i = 1;
		while (i <= 10)
		{
			System.out.println(i+"사랑해요");
			i++;
		}
		
		
	}

}
